// Sample reducer
export function counterReducer(state, action) {
    switch (action.type) {
        case 'INCREMENT':
            return { ...state, count: state.count + 1 };
        case 'DECREMENT':
            return { ...state, count: state.count - 1 };

        // Add your RESET and SET cases here

        default:
            return state;
    }
}
