import { useReducer } from 'react';
import { counterReducer } from './reducer';

function App() {
    const [state, dispatch] = useReducer(counterReducer, { count: 0 });

    return (
        <div className="App">
            <h1>Reducer Tutorial with Counter Example</h1>
            <div>
            <h2>Count: {state.count}</h2>
            <button className="button" onClick={() => dispatch({ type: 'INCREMENT' })}>Increment</button>
            <button className="button" onClick={() => dispatch({ type: 'DECREMENT' })}>Decrement</button>
            <button className="button" onClick={() => dispatch({ type: 'RESET' })}>Reset</button>
            <button className="button" onClick={() => dispatch({ type: 'SET', payload: 10 })}>Set to 10</button>
        </div>
        </div>
    );
}

export default App;

/reducer.js:
// Sample reducer
export function counterReducer(state, action) {
  switch (action.type) {
      case 'INCREMENT':
          return { ...state, count: state.count + 1 };
      case 'DECREMENT':
          return { ...state, count: state.count - 1 };
      case 'RESET':
          return { ...state, count: 0 };
      case 'SET':
          return { ...state, count: action.payload };
      default:
          return state;
  }
}
