import { useState } from 'react';

function App() {
    const [count, setCount] = useState(0);

    // event handler
    function increment() {
        setCount(count => count + 1);
    }
    // event handler
    function decrement() {
        setCount(count => count - 1);
    }
    // event handler
    function reset() {
        setCount(() => 0);
    }
    // event handler
    function set(value) {
        setCount(() => value);
    }

    return (
        <div className="App">
            <h1>Reducer Tutorial with Counter Example</h1>
            <div>
            <h2>Count: {count}</h2>
            <button className="button" onClick={increment}>Increment</button>
            <button className="button" onClick={decrement}>Decrement</button>
            <button className="button" onClick={reset}>Reset</button>
            <button className="button" onClick={() => set(10)}>Set to 10</button>
            {/* Add your RESET and SET buttons here */}

        </div>
        </div>
    );
}

export default App;
